	"github.com/pulumi/pulumi/sdk/v2/go/pulumi"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := cloudflare.GetZones(ctx, &cloudflare.GetZonesArgs{
			Filter: cloudflare.GetZonesFilter{
				Name: "example.com",
			},
		}, nil)
		if err != nil {
			return err
		}
		return nil
	})
}
