      mode: whitelist
      configuration:
        target: country
        value: AQ
      zoneId: cb029e245cfdd66dc8d2e570d5dd3322
  officeNetwork:
    type: cloudflare:AccessRule
    properties:
      notes: Requests coming from office network
      mode: whitelist
      configuration:
        target: ip_range
        value:
          Fn::Select:
            - ${count.index}
            - ${myOffice}
    options: {}
