			return err
		}
		_, err = cloudflare.NewWorkerCronTrigger(ctx, "exampleTrigger", &cloudflare.WorkerCronTriggerArgs{
			ScriptName: exampleScript.Name,
			Schedules: pulumi.StringArray{
				pulumi.String("*/5 * * * *"),
				pulumi.String("10 7 * * mon-fri"),
			},
		})
